    Pessoa p = new Pessoa();
    Pessoa2 p2 = new Pessoa2();
    
    QueueAdulto qAdulto = new QueueAdulto();
    QueueCriancas qCriancas = new QueueCriancas();
    QueueIdoso qIdoso = new QueueIdoso();
    QueueRegistrados qRegistrados = new QueueRegistrados();
    int cont = 0;
    
    
    String vetor [] = new String[cont+1];

    private void cadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cadastrarActionPerformed
        // TODO add your handling code here:
        cont++;
       String nome = text_Nome.getText();
        String sexo = text_Sexo.getText();
        int idade = Integer.parseInt(text_Idade.getText());
        String idade2 = text_Idade.getText();

        p.setNome_Candidato(nome);
        p.setSexo(sexo);
        p.setIdade_Candidato(idade);
        
        p2.setNome_Candidato(nome);
        p2.setSexo(sexo);
        p2.setIdade_Candidato(idade2);

        if (p.getIdade_Candidato() < 15) {
            qCriancas.enqueue(p.toString());
        } else if (p.getIdade_Candidato() >= 15 && p.getIdade_Candidato() < 59) {
            qAdulto.enqueue(p.toString());
        } else {
            qIdoso.enqueue(p.toString());
        }
        
        qRegistrados.enqueue(p.toString());
        vetor[cont] = p2.toString();
        
        text_Nome.setText("");
        text_Idade.setText("");
        text_Sexo.setText("");
    }//GEN-LAST:event_cadastrarActionPerformed

    private void AtenderActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_AtenderActionPerformed
        // TODO add your handling code here:
        if (!qRegistrados.isEmpty()) {
            if (!qIdoso.isEmpty()) {
                visor.setText((String) qIdoso.dequeue());
            } else if (!qCriancas.isEmpty()) {
                visor.setText((String) qCriancas.dequeue());
            } else if (!qAdulto.isEmpty()) {
                visor.setText((String) qAdulto.dequeue());
            }
        } else if (qRegistrados.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Sem Pacientes\nNo momento");
        }
    }//GEN-LAST:event_AtenderActionPerformed

