    private void ordernadosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ordernadosActionPerformed
        // TODO add your handling code here:
        String aux = "";
        for (int i = 0; i < vetor.length; i++)
            {
                for (int j = 0; j < vetor.length; j++)
                {
                    if (vetor[i].compareToIgnoreCase(vetor[j]) > 0)
                    {
    //aqui acontece a troca, do maior cara  vaia para a direita e o menor para a esquerda
                        aux = vetor[i];
                        vetor[i] = vetor[j];
                        vetor[j] = aux;
                    }
                }
            }
    }//GEN-LAST:event_ordernadosActionPerformed

